section.script:
 function autofull() {
 var image=document.getElementById('screenshot');

 image.style.width='inherit'; 
 image.style.height='inherit';
};

 function autoscroll() {
   var image=document.getElementById('screenshot');

   image.style.height='300px';
   image.scrollLeft=300; 
   image.scrollTop=94;   
 };
 autoscroll();
